#pragma config(Sensor, dgtl1,  leftEncoder,    sensorQuadEncoder)
#pragma config(Sensor, dgtl3,  rightEncoder,   sensorQuadEncoder)
#pragma config(Motor,  port1,           leftMotor,     tmotorVex393_HBridge, openLoop)
#pragma config(Motor,  port2,           rightMotor,    tmotorVex393_MC29, openLoop, reversed)
//*!!Code automatically generated by 'ROBOTC' configuration wizard               !!*//

#include "CLibrary.c"

// Notes:
// You can download files from cortex using Robot > File Management
// You can check battery level by using nImmediateBatterLevel; Found in Battery > Variables

task MotorSpeed();
task main() {
	SetAverageDelay(20);
	SetLeftMotor(port1);
	SetRightMotor(port2);
	SetLeftEncoder(leftEncoder);
	SetRightEncoder(rightEncoder);
	SetControllerSpeed(1);
	//startTask(GamerControl);
	//startTask(MotorSpeed);

	while(true) {
		// Keep program alive.
		delay(GetDelay());
	}
}

task MotorSpeed() {
	while(true){
		if(vexRT[Btn8D]){
			SetControllerSpeed(2);
		}
		else {
			SetControllerSpeed(1);
		}

		delay(GetDelay());
	}
}
